<style>
  :root {
    --ai-dr-bg: #fefefe;
    --ai-dr-bg-hover: #fef2f2;
    --ai-dr-fg: #1f2937;
    --ai-dr-primary: #dc2626; /* Á∫¢Ëâ≤‰∏ªËâ≤Ë∞É */
    --ai-dr-border: #e5e7eb;
    --ai-dr-menu-bg: #ffffff;
    --ai-dr-item-hover: #fee2e2;
    --ai-dr-shadow: 0 6px 25px rgba(0,0,0,.08);
  }

  .ai-deep-reading { position: relative; display: inline-block; }

  .ai-dr__btn {
    display: inline-flex;
    align-items: center;
    gap: .5rem;
    background: var(--ai-dr-bg);
    color: var(--ai-dr-fg);
    border: 1px solid var(--ai-dr-border);
    padding: .6rem .9rem;
    border-radius: 999px;
    cursor: pointer;
    box-shadow: 0 2px 8px rgba(0,0,0,.05);
    transition: all .2s ease;
  }
  .ai-dr__btn:hover {
    background: var(--ai-dr-bg-hover);
    border-color: var(--ai-dr-primary);
    box-shadow: 0 3px 12px rgba(220,38,38,.15);
  }
  .ai-dr__icon, .ai-dr__chevron { display: inline-flex; color: var(--ai-dr-primary); }
  .ai-dr__label { font-weight: 600; }

  .ai-dr__menu {
    position: absolute;
    top: calc(100% + 8px);
    left: 0;
    min-width: 230px;
    background: var(--ai-dr-menu-bg);
    border: 1px solid var(--ai-dr-border);
    border-radius: 10px;
    padding: .4rem;
    display: none;
    box-shadow: var(--ai-dr-shadow);
    z-index: 30;
  }
  .ai-dr__menu[aria-hidden="false"] { display: block; }

  .ai-dr__item {
    display: flex;
    align-items: center;
    gap: .7rem;
    padding: .55rem .6rem;
    border-radius: 8px;
    color: var(--ai-dr-fg);
    text-decoration: none;
    transition: background .15s ease;
  }
  .ai-dr__item:hover { background: var(--ai-dr-item-hover); }
  .ai-dr__item-icon {
    width: 22px; height: 22px;
    display: flex; align-items: center; justify-content: center;
    color: var(--ai-dr-primary);
  }
  .ai-dr__title { font-weight: 500; font-size: .95rem; }

  @media (max-width: 640px) {
    .ai-dr__menu { right: 0; left: auto; width: min(90vw, 300px); }
  }
</style>

<script>
(function(){
  var root = document.getElementById('ai-deep-reading');
  if(!root) return;
  var btn = root.querySelector('.ai-dr__btn');
  var menu = root.querySelector('.ai-dr__menu');
  function toggle(){
    var open = menu.getAttribute('aria-hidden') !== 'false';
    menu.setAttribute('aria-hidden', open ? 'false' : 'true');
    btn.setAttribute('aria-expanded', open ? 'true' : 'false');
  }
  btn.addEventListener('click', function(e){ e.preventDefault(); toggle(); });
  document.addEventListener('click', function(e){ if(!root.contains(e.target)) menu.setAttribute('aria-hidden','true'); });
})();
</script>

{%- liquid
  assign author_ref = article.metafields.custom.author
  assign author = nil
  if author_ref and author_ref.value
    assign author = author_ref.value
  endif
-%}

{%- if author -%}
  <section class="article-author" aria-label="Article author">
    {%- if author.avatar -%}
      <img
        class="author-avatar"
        src="{{ author.avatar | image_url: width: 160 }}"
        alt="{{ author.name | escape }}"
        loading="lazy">
    {%- endif -%}

    <div class="author-info">
      <h4 class="author-name">{{ author.name }}</h4>

      {%- if author.title -%}
        <div class="author-title">{{ author.title }}</div>
      {%- endif -%}

      {%- if author.bio -%}
        <div class="author-bio">{{ author.bio | metafield_tag }}</div>
      {%- endif -%}

      {%- if author.website or author.twitter or author.facebook or author.linkedin or author.email -%}
        <div class="author-social" role="group" aria-label="Author social links">
          {%- if author.website -%}
            {%- capture site_url -%}
              {%- if author.website contains '://' -%}{{ author.website }}{%- else -%}https://{{ author.website }}{%- endif -%}
            {%- endcapture -%}
            <a href="{{ site_url | strip }}"
               class="social-link"
               target="_blank" rel="nofollow noopener"
               aria-label="Website" title="Website">üåê</a>
          {%- endif -%}

          {%- if author.facebook -%}
            <a href="{{ author.facebook }}" class="social-link facebook" target="_blank" rel="nofollow noopener" aria-label="Facebook" title="Facebook">
              <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor" aria-hidden="true">
                <path d="M22 12a10 10 0 1 0-11.5 9.9v-7H8v-3h2.5V9.5a3.5 3.5 0 0 1 3.8-3.9c1 0 2 .2 2 .2v2.2H15.9c-1.2 0-1.6.8-1.6 1.6V12h2.7l-.4 3h-2.3v7A10 10 0 0 0 22 12z"/>
              </svg>
            </a>
          {%- endif -%}

          {%- if author.twitter -%}
            <a href="{{ author.twitter }}" class="social-link twitter" target="_blank" rel="nofollow noopener" aria-label="Twitter" title="Twitter">
              <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor" aria-hidden="true">
                <path d="M22.46 6c-.77.35-1.6.58-2.46.69a4.28 4.28 0 0 0 1.88-2.37c-.83.5-1.76.86-2.75 1.05a4.26 4.26 0 0 0-7.3 3.89A12.1 12.1 0 0 1 3.1 4.67a4.27 4.27 0 0 0 1.32 5.7A4.2 4.2 0 0 1 2.8 9v.05a4.27 4.27 0 0 0 3.42 4.18c-.4.11-.82.16-1.25.16a4.22 4.22 0 0 1-.8-.07 4.26 4.26 0 0 0 3.98 2.96 8.54 8.54 0 0 1-5.3 1.83c-.34 0-.68-.02-1.02-.06a12.06 12.06 0 0 0 6.54 1.92c7.85 0 12.15-6.5 12.15-12.14 0-.18-.01-.36-.02-.54A8.64 8.64 0 0 0 22.46 6z"/>
              </svg>
            </a>
          {%- endif -%}

          {%- if author.linkedin -%}
            <a href="{{ author.linkedin }}" class="social-link linkedin" target="_blank" rel="nofollow noopener" aria-label="LinkedIn" title="LinkedIn">
              <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor" aria-hidden="true">
                <path d="M19 3A2 2 0 0 1 21 5v14a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h14ZM8.3 18V10H5.7v8h2.6Zm-1.3-9a1.3 1.3 0 1 0 0-2.6 1.3 1.3 0 0 0 0 2.6Zm11 9v-4.4c0-2.3-2.5-2.1-3.4-1.1v-1h-2.6v6.5h2.6v-3.7c0-.8.9-1.2 1.5-.8.3.2.5.6.5 1v3.5h2.4Z"/>
              </svg>
            </a>
          {%- endif -%}

          {%- if author.email -%}
            <a href="mailto:{{ author.email | strip }}" class="social-link email" aria-label="Email" title="Email">
              <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor" aria-hidden="true">
                <path d="M20 4H4a2 2 0 0 0-2 2v12a2 2 0 0 0 2 2h16a2 2 0 0 0 2-2V6a2 2 0 0 0-2-2Zm0 2v.5l-8 5-8-5V6h16ZM4 18V8.2l7.4 4.6a1 1 0 0 0 1.2 0L20 8.2V18H4Z"/>
              </svg>
            </a>
          {%- endif -%}
        </div>
      {%- endif -%}
    </div>
  </section>

  <style>
    .article-author {
      display: flex;
      align-items: flex-start;
      gap: 1.5rem;
      padding: 1.5rem;
      background: #f9fafb;
      border-radius: 1rem;
      margin-top: 2rem;
    }

    .author-avatar {
      width: 80px;
      height: 80px;
      border-radius: 9999px;
      object-fit: cover;
    }

    .author-info {
      flex: 1;
    }

    .author-name {
      margin: 0 0 .25rem;
      font-size: 1.125rem;
      font-weight: 600;
    }

    .author-title {
      margin: 0 0 .5rem;
      color: #6b7280;
      font-size: .95rem;
    }

    .author-bio {
      color: #374151;
      font-size: .95rem;
      margin-bottom: .75rem;
    }

    .author-social {
      display: flex;
      align-items: center;
      gap: .5rem;
      flex-wrap: wrap;
    }

    .social-link {
      display: inline-grid;
      place-items: center;
      width: 32px;
      height: 32px;
      border-radius: 9999px;
      background: #6b7280;
      color: #fff;
      text-decoration: none;
      transition: background .2s ease, transform .2s ease;
    }

    .social-link:hover { transform: translateY(-1px); }

    .social-link.facebook:hover { background: #1877f2; }
    .social-link.twitter:hover  { background: #1da1f2; }
    .social-link.linkedin:hover { background: #0077b5; }
    .social-link.email:hover    { background: #ea4335; }
  </style>
{%- endif -%}

{% if article.tags.size > 0 %}
  <div class="article-tags" style="margin-top: 30px;">
    <h4 style="margin-bottom: 12px;">Tags:</h4>
    <ul class="tag-list">
      {% for tag in article.tags %}
        <li>
          <a href="{{ blog.url }}/tagged/{{ tag | handleize }}" class="tag-btn">{{ tag }}</a>
        </li>
      {% endfor %}
    </ul>
  </div>
{% endif %}

{% assign recent_posts = blogs['ume-all-posts'].articles | slice: 0, 4 %}
{% if recent_posts.size > 0 %}
  <div class="recent-articles" style="margin-top: 40px;">
    <h3 style="margin-bottom: 16px;">Latest Posts</h3>
    <div class="recent-article-list">
      {% for post in recent_posts %}
        <div class="recent-post-card">
          <a href="{{ post.url }}" class="post-image-link">
            {% if post.image %}
              <img src="{{ post.image | img_url: '600x400', crop: 'center' }}" alt="{{ post.title }}" class="post-image">
            {% endif %}
          </a>
          <div class="post-info">
            <a href="{{ post.url }}" class="post-title">{{ post.title }}</a>
            <div class="post-date">
              {{ post.published_at | date: "%B %d, %Y" }}
            </div>
            {% if post.tags.size > 0 %}
              <div class="post-tags">
                {% for tag in post.tags %}
                  <a href="{{ blog.url }}/tagged/{{ tag | handleize }}" class="tag-btn">{{ tag }}</a>
                {% endfor %}
              </div>
            {% endif %}
          </div>
        </div>
      {% endfor %}
    </div>
  </div>
{% endif %}
